import React, { Component } from 'react';
import './App.css';
import { Button } from './components/Button';
import { EmptyButton } from './components/EmptyButton';

import { Input } from './components/Input';
import { ClearButton } from './components/ClearButton';
import * as math from 'mathjs';
let record;
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: "",
      history:"",
      calculated:false
    }
  }

  addToInput = val => {
    this.setState({input: this.state.input + val});
    this.setState({history: this.state.history + val});
    console.log('history:',this.state.history);
  }



  handleEqual = () => {
      this.setState({ input: math.eval(this.state.input) });
      this.setState({history: this.state.history + '='+math.eval(this.state.input)  });

      console.log('history ==:',this.state.history)
      this.setState({calculated:true});
      this.setState({history:this.state.history+','})
      localStorage.setItem('history', JSON.stringify( this.state.history) );
      this.setState({calculated:false});
  }

  handleMultiply = val => {
    if (val === "x") {
      this.setState({input: this.state.input + val.replace("x", "*")});
      this.setState({history: this.state.history + val});
      console.log('history:',this.state.history);
    }
}
  handlereverse = val => {
    if (val === "+/-") {
      var result = this.state.input*(-1);
      this.setState({input: result});
      this.setState({history: this.state.history + val});
      console.log('history:',this.state.history);
    }
  }
