import React, { Component } from 'react';
import './App.css';
import { Button } from './components/Button';
import { Input } from './components/Input';
import { ClearButton } from './components/ClearButton';
import * as math from 'mathjs';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: ""
    }
  }

  addToInput = val => {
    this.setState({input: this.state.input + val});
  }

  handleEqual = () => {
    this.setState({ input: math.eval(this.state.input) });
  }

  handleMultiply = val => {
    if (val === "x") {
      this.setState({input: this.state.input + val.replace("x", "*")});
    }
}
  handlereverse = val => {
    if (val === "+/-") {
      var result = this.state.input*(-1);
      this.setState({input: result});
    }
  }
  render() {
    return (
      <div className="app">
        <div className="calculator-wrapper-science">
          <Input></Input>
          <div className="row">
            <ClearButton className="scienceGrey">(</ClearButton>
            <Button className="scienceGrey">)</Button>
            <Button className="scienceGrey">mc</Button>
            <Button className="scienceGrey">m+</Button>
            <Button className="scienceGrey">m-</Button>
            <Button className="scienceGrey">mr</Button>
          </div>
          <div className="row">
            <Button className="scienceGrey">2<sup>incl</sup></Button>
            <Button className="scienceGrey">x<sup>2</sup></Button>
            <Button className="scienceGrey">x<sup>3</sup></Button>
            <Button className="scienceGrey">x<sup>y</sup></Button>
            <Button className="scienceGrey">e<sup>x</sup></Button>
            <Button className="scienceGrey">10<sup>x</sup></Button>
          </div>
          <div className="row">
            <Button className="scienceGrey">1/x</Button>
            <Button className="scienceGrey">2√x</Button>
            <Button className="scienceGrey">3√x</Button>
            <Button className="scienceGrey">y√x</Button>
            <Button className="scienceGrey">ln</Button>
            <Button className="scienceGrey">log<sub>10</sub></Button>
          </div>
          <div className="row">
            <Button className="scienceGrey">x!</Button>
            <Button className="scienceGrey">sin</Button>
            <Button className="scienceGrey">cos</Button>
            <Button className="scienceGrey">tan</Button>
            <Button className="scienceGrey">e</Button>
            <Button className="scienceGrey">EE</Button>
          </div>
          <div className="row">
            <Button className="scienceGrey">Rad</Button>
            <Button className="scienceGrey">sinh</Button>
            <Button className="scienceGrey">cosh</Button>
            <Button className="scienceGrey">tanh</Button>
            <Button className="scienceGrey">π</Button>
            <Button className="scienceGrey">Rand</Button>
          </div>
        </div>
        <div className="calculator-wrapper">
          <Input input={this.state.input}></Input>
          <div className="row">
            <ClearButton handleClear={() => this.setState({input: ""})}>C</ClearButton>
            <Button className="greyBtn" handleClick={this.handlereverse}>+/-</Button>
            <Button className="greyBtn" handleClick={this.addToInput}>%</Button>
            <Button handleClick={this.addToInput}>/</Button>
          </div>
          <div className="row">
            <Button handleClick={this.addToInput}>7</Button>
            <Button handleClick={this.addToInput}>8</Button>
            <Button handleClick={this.addToInput}>9</Button>
            <Button handleClick={this.handleMultiply}>x</Button>
          </div>
          <div className="row">
            <Button handleClick={this.addToInput}>4</Button>
            <Button handleClick={this.addToInput}>5</Button>
            <Button handleClick={this.addToInput}>6</Button>
            <Button handleClick={this.addToInput}>-</Button>
          </div>
          <div className="row">
            <Button handleClick={this.addToInput}>1</Button>
            <Button handleClick={this.addToInput}>2</Button>
            <Button handleClick={this.addToInput}>3</Button>
            <Button handleClick={this.addToInput}>+</Button>
          </div>
          <div className="row">
            <Button className="lgCircle" handleClick={this.addToInput}>0</Button>
            <Button handleClick={this.addToInput}>.</Button>
            <Button handleClick={() => this.handleEqual()}>=</Button>
          </div>

        </div>
      </div>
    );
  }
}

export default App;
